import React from "react";
import "./Repos.css";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import { Link } from "react-router-dom";

export default function Repos(props) {
  const displayRepos = (props) => {
    const { repos } = props;

    // Sorting repos by Star count in descending order then mapping through data
    if (repos.length > 0) {
      repos.sort((a, b) => {
        return b.stargazers_count - a.stargazers_count;
      });
      return repos.map((repo) => {
        return (
          <div className="repo-card" key={repo.id}>
            <div>
              <Card style={{ width: "100%", height: "auto" }}>
                {/* <Card.Img variant="top" src="img/logo192.png" /> */}
                <Card.Body>
                  <Card.Title>
                    <h1>
                      <a>{repo.name}</a>
                    </h1>
                  </Card.Title>
                  <Card.Text>{repo.description}</Card.Text>
                  <div id="grid" className="pt-3">
                    <Card.Text>
                      <b>
                        <i>{repo.language}</i>
                      </b>
                    </Card.Text>
                    <Card.Text>Star Count: {repo.stargazers_count}</Card.Text>
                    <Card.Text>Fork Count: {repo.forks_count}</Card.Text>
                    <Card.Text>Date Created: {repo.created_at}</Card.Text>
                  </div>
                  <Link to={`/commits/${repo.name}`}>
                    <Button variant="primary">Go somewhere</Button>
                  </Link>
                </Card.Body>
              </Card>
            </div>
          </div>
        );
      });
    } else {
      return <h3>No Repos Listed</h3>;
    }
  };

  return displayRepos(props);
}
