import "./App.css";
import { useEffect } from "react";
import { useState } from "react";
import Repos from "./Repos";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";

function App() {
  const [repos, setRepos] = useState();

  const url = "https://api.github.com/orgs/Netflix/repos";

  async function getAllRepos() {
    axios
      .get(`${url}`)
      .then((res) => {
        // console.log(res);
        const allRepos = res.data;
        // console.log(allRepos);
        setRepos(allRepos);
      })
      .catch((error) => console.error(`Error: ${error}`));
  }

  useEffect(() => {
    getAllRepos();
  }, []);

  return (
    <>
      <div className="App w-100 min-vh-100 justify-content-center align-items-center d-flex">
        {repos?.map((repo) => {
          console.log(repo.name);
          <Card style={{ width: "18rem" }}>
            <Card.Img variant="top" src="img/logo192.png" />
            <Card.Body>
              <Card.Title key={repo.id}>{repo.name}</Card.Title>
              <Card.Text>
                Some quick example text to build on the card title and make up
                the bulk of the card's content.
              </Card.Text>
              <Button variant="primary">Go somewhere</Button>
            </Card.Body>
          </Card>;
        })}
        {/* {repos.length ? (
          <>
            {repos.map((repo) => {
              <h1>{repo.name}</h1>;
            })}
          </>
        ) : (
          <div>Loading....</div>
        )} */}
        {/* {JSON.stringify(repos)} */}
      </div>
      <div className="section">
        <Repos repos={repos} />
      </div>
    </>
  );
}

export default App;
