import React from "react";
import "./Repos.css";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import { Container } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

export default function Repos(props) {
  //   console.log(props.repos);

  const displayRepos = (props) => {
    const { repos } = props;

    console.log(repos);

    if (repos.length > 0) {
      repos.sort((a, b) => {
        return b.stargazers_count - a.stargazers_count;
      });
      return repos.map((repo) => {
        return (
          <div
            //   App w-100 min-vh-100 justify-content-center align-items-center d-flex
            className="repo-card"
            key={repo.id}
          >
            <div>
              <Card style={{ width: "100%", height: "auto" }}>
                {/* <Card.Img variant="top" src="img/logo192.png" /> */}
                <Card.Body>
                  <Card.Title>
                    <h1>{repo.name}</h1>
                  </Card.Title>
                  <Card.Text>
                    <b>
                      <i>{repo.language}</i>
                    </b>
                  </Card.Text>
                  <Card.Text>{repo.description}</Card.Text>
                  <Card.Text>Star Count: {repo.stargazers_count}</Card.Text>
                  <Card.Text>Fork Count: {repo.forks_count}</Card.Text>
                  <Card.Text>Date Created: {repo.created_at}</Card.Text>
                  <Link to={`/commits/${repo.id}`}>
                    <Button variant="primary" onClick={handleClick(repo)}>
                      Go somewhere
                    </Button>
                  </Link>
                </Card.Body>
              </Card>
            </div>
          </div>
        );
      });
    } else {
      return <h3>No Repos yet</h3>;
    }
  };

  return displayRepos(props);
}
