import React from "react";
import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";

export default function Commits(props) {
  const params = useParams();
  const name = params.id;
  // const { repos } = props;
  // let setRepo = {};
  // console.log(params.id);

  const [commits, setCommits] = useState("");

  // if (repos.length > 0) {
  //   repos.map((repo) => {
  //     if (repo.id == params.id) {
  //       setRepo = repo;
  //     }
  //   });
  // }
  const url = `https://api.github.com/repos/Netflix/${name}/commits`;

  async function getRepoData() {
    axios
      .get(`${url}`)
      .then((res) => {
        // console.log(res);
        const allCommits = res.data;
        console.log(allCommits);
        setCommits(allCommits);
      })
      .catch((error) => console.error(`Error: ${error}`));
  }

  useEffect(() => {
    getRepoData();
  }, []);

  const displayCommits = (commits) => {
    if (commits.length > 0) {
      commits.map((commit) => {
        console.log(commit.commit.author.name);
        console.log(commit.commit.author.date);
        return (
          <div
            //   App w-100 min-vh-100 justify-content-center align-items-center d-flex
            className="repo-card"
            key={commit.id}
          >
            <div>
              <Card style={{ width: "100%", height: "auto" }}>
                {/* <Card.Img variant="top" src="img/logo192.png" /> */}
                <Card.Body>
                  <Card.Title>
                    <h1>{commit.commit.message}</h1>
                  </Card.Title>
                  <Card.Text>
                    <b>
                      <i>{commit.commit.author.name}</i>
                    </b>
                  </Card.Text>
                  <Card.Text>{commit.commit.author.date}</Card.Text>
                </Card.Body>
              </Card>
            </div>
          </div>
        );
      });
    }
  };

  return displayCommits(commits);
}
